// src/pages/HomePage.js

import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const HomePage = () => {
  const [hoveredCard, setHoveredCard] = useState(null);
  const [hoveredImage, setHoveredImage] = useState(null);
  const [backgroundColor, setBackgroundColor] = useState('#000000'); // Default to black

  const handleCardMouseEnter = (index) => setHoveredCard(index);
  const handleCardMouseLeave = () => setHoveredCard(null);
  
  const handleImageMouseEnter = (index) => setHoveredImage(index);
  const handleImageMouseLeave = () => setHoveredImage(null);
  
  const handleBackgroundColorChange = (color) => {
    setBackgroundColor(color);
  };

  // Determine theme based on background color
  const isDarkTheme = backgroundColor === '#000000';

  return (
    <div style={{ ...styles.container, backgroundColor }}>
      <header style={isDarkTheme ? styles.headerDark : styles.headerLight}>
        <h1>Welcome to Our Hotel Booking System</h1>
        <p>Book your stay at our luxurious hotels with ease.</p>
        
        {/* Color Switch */}
        <div style={styles.colorSwitch}>
          <button onClick={() => handleBackgroundColorChange('#ffffff')} style={styles.colorButton}>
            White Background
          </button>
          <button onClick={() => handleBackgroundColorChange('#000000')} style={styles.colorButton}>
            Black Background
          </button>
        </div>
      </header>
      
      {/* Home Page Welcome Section */}
      <section>
        <h2 style={{ color: isDarkTheme ? '#ffffff' : '#000000' }}>Home Page</h2>
        <p style={{ color: isDarkTheme ? '#ffffff' : '#000000' }}>Welcome to the home page!</p>
      </section>

      {/* Horizontal Scroll of Hotel Images */}
      <section style={styles.hotelGallery}>
        <h2 style={{ color: isDarkTheme ? '#ffffff' : '#000000' }}>Explore Our Hotels</h2>
        <div style={styles.hotelList}>
          {hotelData.map((hotel, index) => (
            <div
              key={index}
              style={{
                ...styles.hotelCard,
                ...(hoveredCard === index && styles.hotelCardHover),
                backgroundColor: isDarkTheme ? '#1a1a1a' : '#f8f9fa', // Card color based on theme
                color: isDarkTheme ? '#ffffff' : '#000000', // Text color based on theme
              }}
              onMouseEnter={() => handleCardMouseEnter(index)}
              onMouseLeave={handleCardMouseLeave}
            >
              <img
                src={hotel.image}
                alt={hotel.name}
                style={{
                  ...styles.hotelImage,
                  ...(hoveredImage === index && styles.hotelImageHover),
                }}
                onMouseEnter={() => handleImageMouseEnter(index)}
                onMouseLeave={handleImageMouseLeave}
              />
              <div style={styles.hotelInfo}>
                <h3 style={{ color: isDarkTheme ? '#ffffff' : '#000000' }}>{hotel.name}</h3>
                <p style={{ color: isDarkTheme ? '#ffffff' : '#000000' }}>{hotel.description}</p>
                <Link to="/book" style={styles.bookButton}>Book Now</Link>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* About Us Section */}
      <section style={styles.aboutSection}>
        <h2 style={{ color: isDarkTheme ? '#ffffff' : '#000000' }}>About Us</h2>
        <p style={{ color: isDarkTheme ? '#ffffff' : '#000000' }}>
          We are a leading hotel booking service offering luxurious stays at prime locations around the world. Our mission is to provide an exceptional hospitality experience to our guests.
        </p>
        <Link to="/about" style={styles.infoButton}>Learn More</Link>
      </section>

      {/* Contact Us Section */}
      <section style={styles.contactSection}>
        <h2 style={{ color: isDarkTheme ? '#ffffff' : '#000000' }}>Contact Us</h2>
        <p style={{ color: isDarkTheme ? '#ffffff' : '#000000' }}>
          If you have any questions or need assistance, feel free to reach out to our support team.
        </p>
        <Link to="/contact" style={styles.infoButton}>Get in Touch</Link>
      </section>
    </div>
  );
};

export default HomePage;

const hotelData = [
  { name: 'Hotel Grand Deluxe', image: 'data:image/jpeg;base64,', description: 'Luxury hotel with beautiful views and excellent service.' },
  { name: 'Sea Breeze Resort', image: 'data:image/jpeg;base64,', description: 'Relax by the sea with our exclusive beach-side amenities.' },
  { name: 'Mountain Retreat', image: 'data:image/jpeg;base64,', description: 'Escape to the mountains for a serene and peaceful stay.' },
  { name: 'City Center Inn', image: 'data:image/jpeg;base64,', description: 'Conveniently located in the heart of the city.' },
  { name: 'Desert Oasis', image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTy4pVx04fJ5ba_8Ardab9AThS5DZblaI8irQ&s', description: 'Experience the desert like never before with our unique accommodations.' },
];

